import 'package:flutter/material.dart';
import 'books_data.dart';

void main() {
  runApp(MyApp());
}

class MyApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    final title = "Daftar Buku";

    return MaterialApp(
        title: title,
        home: Scaffold(
            appBar: AppBar(
              title: Text(title),
            ),
            body: GridView.count(
                crossAxisCount: 2,
                children: List.generate(choices.length, (index) {
                  return Center(
                    child: ChoiceCard(choice: choices[index]),
                  );
                }
                )
            )
        )
    );
  }
}

class Choice {
  const Choice({required this.title, required this.icon});
  final String title;
  final String icon;
}

const List<Choice> choices = const [
  const Choice(title: 'Learning React Native', icon: "https://m.media-amazon.com/images/I/91PhGP7QjbL._AC_UL320_.jpg"),
  const Choice(title: 'Full-Stack React, TypeScript, and Node', icon: "data:image/jpeg;base64,"),
  const Choice(title: 'React and React Native', icon: "https://images-na.ssl-images-amazon.com/images/I/51noIwSubgL._SX404_BO1,204,203,200_.jpg"),
  const Choice(title: 'React Cookbook', icon: "data:image/jpeg;base64,"),
  const Choice(title: 'Recent Aplications of Selected Name Reactions in the Total Synthesis of Alkaloids', icon: "https://images-na.ssl-images-amazon.com/images/I/41cje+t8k4S._SX329_BO1,204,203,200_.jpg"),
  const Choice(title: 'Pro MERN Stack', icon: "https://m.media-amazon.com/images/I/41bCSpeERKL._AC_SY780_.jpg"),
  const Choice(title: 'Getting Started with React', icon: "https://images-na.ssl-images-amazon.com/images/I/41Sk3b+icIL._SX404_BO1,204,203,200_.jpg"),
  const Choice(title: 'Beginning React', icon: "https://images-na.ssl-images-amazon.com/images/I/41bwAtbBPdL._SX404_BO1,204,203,200_.jpg"),
  const Choice(title: 'Hands-On Full Stack Development with Spring Boot 2 and React', icon: "https://images-na.ssl-images-amazon.com/images/I/51R1vbU6EQL._SX404_BO1,204,203,200_.jpg"),
];

class ChoiceCard extends StatelessWidget {
  const ChoiceCard({Key? key, required this.choice}) : super(key: key);

  final Choice choice;

  @override
  Widget build(BuildContext context) {
    return Card(
        color: Colors.white,
        child: Center(
          child: Column(
              mainAxisSize: MainAxisSize.min,
              crossAxisAlignment: CrossAxisAlignment.center,
              children: [
                Image.network(choice.icon),
                Text(choice.title),
              ]),
        ));
  }
}